import logo from './logo.svg';
import './App.css';
import Test from './Test'
import React, { useEffect, useState } from 'react';
import { w3cwebsocket as W3CWebSocket } from "websocket";

const App = () => {
    const [established, setEstablished] = useState(false)
    const [payload, setPayload] = useState([]);
    const [text, setText] = useState("");
    const [currentUser, setCurrentUser] = useState(`user_${Math.random() * (999999999 - 1000000)}`)
    const [client, setClient] = useState();

    useEffect(() => {
      if (!established) {
        const client = new W3CWebSocket('ws://localhost:8999/');
        setClient(client)
        setEstablished(true);
      }
    })
    client.onmessage = (message) => {
      console.log(message);
    };

    const handleChange = (e) => {
      setText(e.target.value)
    }

    const handleSubmit = (e) => {
      setPayload([...payload, { user: currentUser, text: text }]);
      client.send(text)
    }

    return (
      <>
        {payload && payload.map(item => (
          <div>
            {/* {item.user} */}
            {item.text}
          </div>
        ))}
        <input onChange={e => handleChange(e)} value={text}></input>
        <button onClick={handleSubmit}>Submit</button>
      </>
    )
}

export default App;
