// import Test from './Test'
import React, { useEffect, useState } from 'react';
import { w3cwebsocket as W3CWebSocket } from "websocket";

const Test = ({...props}) => {
    const [established, setEstablished] = useState(false)
    const [payload, setPayload] = useState([]);
    const [text, setText] = useState("");
    const [currentUser, setCurrentUser] = useState(`user_${Math.random() * (999999999 - 1000000)}`)
    useEffect(() => {
      if (props.client) {
        props.client.onmessage = (message) => {
          setPayload([...payload, {user: message.user, text: message.data.text}])
        };
      }
    })
    console.log(payload)

    const handleChange = (e) => {
      setText(e.target.value)
    }

    const handleSubmit = (e) => {
      props.client.send(JSON.stringify({text: text, user: currentUser}))
      setText("")

    }

    return (
      <>
        {payload && payload.map(item => (
          <div>
            {/* {item.user} */}
            {item.text}
          </div>
        ))}
        <input onChange={e => handleChange(e)} value={text}></input>
        <button onClick={handleSubmit}>Submit</button>
      </>
    )
}

export default Test;
